import React, { useState } from 'react';
import { useConfig } from '../hooks/useConfig';

const SetupPage = () => {
  const { config, updateConfig: updateConfigHook, resetConfig, exportConfig } = useConfig();
  const [activeSection, setActiveSection] = useState('basic');
  const [saved, setSaved] = useState(false);

  const updateConfigValue = (section, key, value, subKey = null) => {
    let path;
    if (subKey) {
      path = `${section}.${key}.${subKey}`;
    } else if (section && key) {
      path = `${section}.${key}`;
    } else {
      path = section;
    }
    
    updateConfigHook(path, value);
    setSaved(false);
  };

  const handleExportConfig = () => {
    exportConfig();
    setSaved(true);
    setTimeout(() => setSaved(false), 3000);
  };

  const handleResetConfig = () => {
    if (window.confirm('Are you sure you want to reset all settings to default? This cannot be undone.')) {
      resetConfig();
    }
  };

  const copyToClipboard = () => {
    const configJSON = JSON.stringify(config, null, 2);
    navigator.clipboard.writeText(configJSON).then(() => {
      alert('Configuration copied to clipboard! You can paste this into your siteConfig.js file.');
    });
  };

  const sections = [
    { id: 'basic', name: 'Basic Info', icon: '🏠', color: 'bg-blue-500' },
    { id: 'character', name: 'Character', icon: '🦊', color: 'bg-orange-500' },
    { id: 'social', name: 'Social Media', icon: '📱', color: 'bg-purple-500' },
    { id: 'streaming', name: 'Streaming', icon: '📹', color: 'bg-red-500' },
    { id: 'support', name: 'Support Links', icon: '💰', color: 'bg-green-500' },
    { id: 'contact', name: 'Contact', icon: '📧', color: 'bg-teal-500' },
    { id: 'features', name: 'Features', icon: '⚙️', color: 'bg-gray-500' }
  ];

  const renderBasicInfo = () => (
    <div className="space-y-6">
      <h3 className="text-2xl font-bold text-gray-800 mb-6">Basic Site Information</h3>
      
      <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
        <div>
          <label className="block text-sm font-semibold text-gray-700 mb-2">Site Title</label>
          <input
            type="text"
            value={config.siteTitle}
            onChange={(e) => updateConfigValue('siteTitle', null, e.target.value)}
            className="w-full p-3 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-fox-orange focus:border-transparent"
            placeholder="Fox Shrine VTuber"
          />
        </div>

        <div>
          <label className="block text-sm font-semibold text-gray-700 mb-2">Site URL</label>
          <input
            type="url"
            value={config.siteUrl}
            onChange={(e) => updateConfigValue('siteUrl', null, e.target.value)}
            className="w-full p-3 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-fox-orange focus:border-transparent"
            placeholder="https://foxshrine.com"
          />
        </div>
      </div>

      <div>
        <label className="block text-sm font-semibold text-gray-700 mb-2">Site Description</label>
        <textarea
          value={config.siteDescription}
          onChange={(e) => updateConfigValue('siteDescription', null, e.target.value)}
          rows={3}
          className="w-full p-3 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-fox-orange focus:border-transparent"
          placeholder="Fun, silly adventures from your favorite shrine fox VTuber!"
        />
      </div>
    </div>
  );

  const renderCharacterInfo = () => (
    <div className="space-y-6">
      <h3 className="text-2xl font-bold text-gray-800 mb-6">Character Information</h3>
      
      <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
        <div>
          <label className="block text-sm font-semibold text-gray-700 mb-2">Character Name</label>
          <input
            type="text"
            value={config.character.name}
            onChange={(e) => updateConfig('character', 'name', e.target.value)}
            className="w-full p-3 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-fox-orange focus:border-transparent"
            placeholder="Kitsune"
          />
        </div>

        <div>
          <label className="block text-sm font-semibold text-gray-700 mb-2">Species</label>
          <input
            type="text"
            value={config.character.species}
            onChange={(e) => updateConfig('character', 'species', e.target.value)}
            className="w-full p-3 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-fox-orange focus:border-transparent"
            placeholder="Fox Spirit"
          />
        </div>

        <div>
          <label className="block text-sm font-semibold text-gray-700 mb-2">Debut Year</label>
          <input
            type="text"
            value={config.character.debut}
            onChange={(e) => updateConfig('character', 'debut', e.target.value)}
            className="w-full p-3 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-fox-orange focus:border-transparent"
            placeholder="2024"
          />
        </div>

        <div>
          <label className="block text-sm font-semibold text-gray-700 mb-2">Pronouns</label>
          <input
            type="text"
            value={config.character.pronouns}
            onChange={(e) => updateConfig('character', 'pronouns', e.target.value)}
            className="w-full p-3 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-fox-orange focus:border-transparent"
            placeholder="She/Her"
          />
        </div>
      </div>

      <div>
        <label className="block text-sm font-semibold text-gray-700 mb-2">Character Greeting</label>
        <input
          type="text"
          value={config.character.greeting}
          onChange={(e) => updateConfig('character', 'greeting', e.target.value)}
          className="w-full p-3 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-fox-orange focus:border-transparent"
          placeholder="Welcome to my shrine! 🦊⛩️"
        />
      </div>

      <div>
        <label className="block text-sm font-semibold text-gray-700 mb-2">Character Description</label>
        <textarea
          value={config.character.description}
          onChange={(e) => updateConfig('character', 'description', e.target.value)}
          rows={4}
          className="w-full p-3 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-fox-orange focus:border-transparent"
          placeholder="A mystical fox spirit who loves gaming, chatting with friends, and protecting the ancient shrine."
        />
      </div>
    </div>
  );

  const renderSocialMedia = () => (
    <div className="space-y-6">
      <h3 className="text-2xl font-bold text-gray-800 mb-6">Social Media Links</h3>
      <p className="text-gray-600 mb-6">Update your social media profiles. Use full URLs (including https://).</p>
      
      <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
        {Object.entries(config.socialMedia).map(([platform, url]) => (
          <div key={platform}>
            <label className="block text-sm font-semibold text-gray-700 mb-2 capitalize">
              {platform === 'youtube' ? 'YouTube' : platform === 'tiktok' ? 'TikTok' : platform}
            </label>
            <input
              type="url"
              value={url}
              onChange={(e) => updateConfig('socialMedia', platform, e.target.value)}
              className="w-full p-3 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-fox-orange focus:border-transparent"
              placeholder={`https://${platform}.com/your-username`}
            />
          </div>
        ))}
      </div>
    </div>
  );

  const renderStreaming = () => (
    <div className="space-y-6">
      <h3 className="text-2xl font-bold text-gray-800 mb-6">Streaming Configuration</h3>
      
      <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
        <div>
          <label className="block text-sm font-semibold text-gray-700 mb-2">Twitch Channel Username</label>
          <input
            type="text"
            value={config.streaming.twitchChannel}
            onChange={(e) => updateConfig('streaming', 'twitchChannel', e.target.value)}
            className="w-full p-3 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-fox-orange focus:border-transparent"
            placeholder="foxshrine"
          />
        </div>

        <div>
          <label className="block text-sm font-semibold text-gray-700 mb-2">YouTube Channel Handle</label>
          <input
            type="text"
            value={config.streaming.youtubeChannel}
            onChange={(e) => updateConfig('streaming', 'youtubeChannel', e.target.value)}
            className="w-full p-3 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-fox-orange focus:border-transparent"
            placeholder="@foxshrine"
          />
        </div>

        <div>
          <label className="block text-sm font-semibold text-gray-700 mb-2">Stream Schedule</label>
          <input
            type="text"
            value={config.streaming.streamSchedule}
            onChange={(e) => updateConfig('streaming', 'streamSchedule', e.target.value)}
            className="w-full p-3 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-fox-orange focus:border-transparent"
            placeholder="Tuesday & Friday 7 PM JST"
          />
        </div>

        <div>
          <label className="block text-sm font-semibold text-gray-700 mb-2">Timezone</label>
          <input
            type="text"
            value={config.streaming.timezone}
            onChange={(e) => updateConfig('streaming', 'timezone', e.target.value)}
            className="w-full p-3 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-fox-orange focus:border-transparent"
            placeholder="JST"
          />
        </div>
      </div>

      <div>
        <label className="block text-sm font-semibold text-gray-700 mb-2">Latest Stream Embed URL</label>
        <input
          type="url"
          value={config.streaming.latestStreamEmbed}
          onChange={(e) => updateConfig('streaming', 'latestStreamEmbed', e.target.value)}
          className="w-full p-3 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-fox-orange focus:border-transparent"
          placeholder="https://www.youtube.com/embed/VIDEO_ID"
        />
        <p className="text-sm text-gray-500 mt-1">Use YouTube embed URL format for best results</p>
      </div>
    </div>
  );

  const renderFeatures = () => (
    <div className="space-y-6">
      <h3 className="text-2xl font-bold text-gray-800 mb-6">Feature Toggles</h3>
      <p className="text-gray-600 mb-6">Enable or disable features across your website.</p>
      
      <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
        {Object.entries(config.features).map(([feature, enabled]) => (
          <div key={feature} className="flex items-center justify-between p-4 bg-gray-50 rounded-lg">
            <div>
              <label className="block text-sm font-semibold text-gray-700">
                {feature.replace(/([A-Z])/g, ' $1').replace(/^./, str => str.toUpperCase())}
              </label>
              <p className="text-sm text-gray-500">
                {getFeatureDescription(feature)}
              </p>
            </div>
            <label className="relative inline-flex items-center cursor-pointer">
              <input
                type="checkbox"
                checked={enabled}
                onChange={(e) => updateConfig('features', feature, e.target.checked)}
                className="sr-only peer"
              />
              <div className="w-11 h-6 bg-gray-200 peer-focus:outline-none peer-focus:ring-4 peer-focus:ring-orange-300 rounded-full peer peer-checked:after:translate-x-full peer-checked:after:border-white after:content-[''] after:absolute after:top-[2px] after:left-[2px] after:bg-white after:rounded-full after:h-5 after:w-5 after:transition-all peer-checked:bg-orange-500"></div>
            </label>
          </div>
        ))}
      </div>
    </div>
  );

  const getFeatureDescription = (feature) => {
    const descriptions = {
      showLiveStatus: 'Display live streaming status indicator',
      enableNewsletter: 'Show newsletter signup forms',
      showMerchSection: 'Display merchandise section on homepage',
      enableFanArtGallery: 'Show fan art gallery page in navigation',
      showDonationLinks: 'Display donation/support links',
      enableScheduleCalendar: 'Show stream schedule calendar',
      showSocialShare: 'Enable social media sharing buttons',
      enableComments: 'Enable comment sections on pages',
      showStreamEmbed: 'Show embedded stream player',
      enableDarkMode: 'Enable dark mode toggle'
    };
    return descriptions[feature] || 'Toggle this feature on/off';
  };

  const renderCurrentSection = () => {
    switch (activeSection) {
      case 'basic': return renderBasicInfo();
      case 'character': return renderCharacterInfo();
      case 'social': return renderSocialMedia();
      case 'streaming': return renderStreaming();
      case 'features': return renderFeatures();
      default: return <div className="p-8 text-center text-gray-500">Section coming soon...</div>;
    }
  };

  return (
    <div className="min-h-screen bg-gray-100 py-8">
      <div className="container mx-auto px-4 max-w-7xl">
        <div className="bg-white rounded-xl shadow-lg overflow-hidden">
          {/* Header */}
          <div className="bg-gradient-to-r from-orange-500 to-red-500 text-white p-8">
            <div className="flex items-center justify-between">
              <div>
                <h1 className="text-4xl font-bold mb-2">🦊 Fox Shrine Setup</h1>
                <p className="text-xl opacity-90">Configure your VTuber website settings</p>
              </div>
              <div className="text-right">
                <p className="text-sm opacity-75">Development Mode Only</p>
                <p className="text-xs opacity-50">This page won't appear in production</p>
              </div>
            </div>
          </div>

          <div className="flex flex-col lg:flex-row">
            {/* Sidebar */}
            <div className="lg:w-80 bg-gray-50 border-r border-gray-200">
              <nav className="p-6">
                <h3 className="text-sm font-semibold text-gray-500 uppercase tracking-wider mb-4">Configuration</h3>
                {sections.map((section) => (
                  <button
                    key={section.id}
                    onClick={() => setActiveSection(section.id)}
                    className={`w-full text-left p-4 rounded-xl mb-3 transition-all duration-200 flex items-center ${
                      activeSection === section.id 
                        ? 'bg-orange-500 text-white shadow-lg transform scale-105' 
                        : 'bg-white text-gray-700 hover:bg-gray-100 shadow-sm hover:shadow-md'
                    }`}
                  >
                    <span className="text-2xl mr-4">{section.icon}</span>
                    <span className="font-semibold">{section.name}</span>
                  </button>
                ))}
              </nav>

              {/* Action Buttons */}
              <div className="p-6 border-t border-gray-200 space-y-3">
                <button
                  onClick={saveConfig}
                  className={`w-full px-6 py-3 rounded-lg font-semibold transition-all ${
                    saved 
                      ? 'bg-green-500 text-white' 
                      : 'bg-blue-500 hover:bg-blue-600 text-white shadow-md hover:shadow-lg'
                  }`}
                >
                  {saved ? '✅ Downloaded!' : '💾 Download Config'}
                </button>
                
                <button
                  onClick={copyToClipboard}
                  className="w-full bg-gray-500 hover:bg-gray-600 text-white px-6 py-3 rounded-lg font-semibold transition-all shadow-md hover:shadow-lg"
                >
                  📋 Copy to Clipboard
                </button>
              </div>
            </div>

            {/* Main Content */}
            <div className="flex-1 p-8 lg:p-12">
              {renderCurrentSection()}
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default SetupPage;
