import React, { useState } from 'react';
import { useConfig } from '../hooks/useConfig';

const SetupPage = () => {
  const { 
    config, 
    updateConfig, 
    resetConfig, 
    clearCorruptedConfig, 
    exportConfig,
    loadConfigFromDatabase,
    isLoading,
    isOnline,
    lastSynced
  } = useConfig();
  const [activeSection, setActiveSection] = useState('basic');
  const [saved, setSaved] = useState(false);

  const handleExportConfig = () => {
    exportConfig();
    setSaved(true);
    setTimeout(() => setSaved(false), 3000);
  };

  const handleResetConfig = () => {
    if (window.confirm('Are you sure you want to reset all settings to default? This cannot be undone.')) {
      resetConfig();
    }
  };

  const sections = [
    { id: 'basic', name: 'Basic Info', icon: 'üè†' },
    { id: 'character', name: 'Character', icon: 'ü¶ä' },
    { id: 'social', name: 'Social Media', icon: 'üì±' },
    { id: 'features', name: 'Features', icon: '‚öôÔ∏è' }
  ];

  const renderBasicInfo = () => (
    <div className="space-y-6">
      <h3 className="text-2xl font-bold text-gray-800 mb-6">Basic Site Information</h3>
      
      <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
        <div>
          <label className="block text-sm font-semibold text-gray-700 mb-2">Site Title</label>
          <input
            type="text"
            value={config.siteTitle || ''}
            onChange={(e) => updateConfig('siteTitle', e.target.value)}
            className="w-full p-3 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-orange-500"
            placeholder="Fox Shrine VTuber"
          />
        </div>

        <div>
          <label className="block text-sm font-semibold text-gray-700 mb-2">Site URL</label>
          <input
            type="url"
            value={config.siteUrl || ''}
            onChange={(e) => updateConfig('siteUrl', e.target.value)}
            className="w-full p-3 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-orange-500"
            placeholder="https://foxshrine.com"
          />
        </div>
      </div>

      <div>
        <label className="block text-sm font-semibold text-gray-700 mb-2">Site Description</label>
        <textarea
          value={config.siteDescription || ''}
          onChange={(e) => updateConfig('siteDescription', e.target.value)}
          rows={3}
          className="w-full p-3 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-orange-500"
          placeholder="Fun, silly adventures from your favorite shrine fox VTuber!"
        />
      </div>
    </div>
  );

  const renderCharacterInfo = () => (
    <div className="space-y-6">
      <h3 className="text-2xl font-bold text-gray-800 mb-6">Character Information</h3>
      
      <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
        <div>
          <label className="block text-sm font-semibold text-gray-700 mb-2">Character Name</label>
          <input
            type="text"
            value={config.character?.name || ''}
            onChange={(e) => updateConfig('character.name', e.target.value)}
            className="w-full p-3 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-orange-500"
            placeholder="Kitsune"
          />
        </div>

        <div>
          <label className="block text-sm font-semibold text-gray-700 mb-2">Species</label>
          <input
            type="text"
            value={config.character?.species || ''}
            onChange={(e) => updateConfig('character.species', e.target.value)}
            className="w-full p-3 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-orange-500"
            placeholder="Fox Spirit"
          />
        </div>
      </div>

      <div>
        <label className="block text-sm font-semibold text-gray-700 mb-2">Character Greeting</label>
        <input
          type="text"
          value={config.character?.greeting || ''}
          onChange={(e) => updateConfig('character.greeting', e.target.value)}
          className="w-full p-3 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-orange-500"
          placeholder="Welcome to my shrine! ü¶ä‚õ©Ô∏è"
        />
      </div>

      <div>
        <label className="block text-sm font-semibold text-gray-700 mb-2">Character Description</label>
        <textarea
          value={config.character?.description || ''}
          onChange={(e) => updateConfig('character.description', e.target.value)}
          rows={4}
          className="w-full p-3 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-orange-500"
          placeholder="A mystical fox spirit who loves gaming, chatting with friends, and protecting the ancient shrine."
        />
      </div>
    </div>
  );

  const renderSocialMedia = () => (
    <div className="space-y-6">
      <h3 className="text-2xl font-bold text-gray-800 mb-6">Social Media Links</h3>
      <p className="text-gray-600 mb-6">Update your social media profiles. Changes apply instantly!</p>
      
      <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
        <div>
          <label className="block text-sm font-semibold text-gray-700 mb-2">YouTube</label>
          <input
            type="url"
            value={config.socialMedia?.youtube || ''}
            onChange={(e) => updateConfig('socialMedia.youtube', e.target.value)}
            className="w-full p-3 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-orange-500"
            placeholder="https://youtube.com/@your-channel"
          />
        </div>

        <div>
          <label className="block text-sm font-semibold text-gray-700 mb-2">Twitch</label>
          <input
            type="url"
            value={config.socialMedia?.twitch || ''}
            onChange={(e) => updateConfig('socialMedia.twitch', e.target.value)}
            className="w-full p-3 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-orange-500"
            placeholder="https://twitch.tv/your-channel"
          />
        </div>

        <div>
          <label className="block text-sm font-semibold text-gray-700 mb-2">Twitter</label>
          <input
            type="url"
            value={config.socialMedia?.twitter || ''}
            onChange={(e) => updateConfig('socialMedia.twitter', e.target.value)}
            className="w-full p-3 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-orange-500"
            placeholder="https://twitter.com/your-handle"
          />
        </div>

        <div>
          <label className="block text-sm font-semibold text-gray-700 mb-2">Discord</label>
          <input
            type="url"
            value={config.socialMedia?.discord || ''}
            onChange={(e) => updateConfig('socialMedia.discord', e.target.value)}
            className="w-full p-3 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-orange-500"
            placeholder="https://discord.gg/your-server"
          />
        </div>
      </div>
    </div>
  );

  const renderFeatures = () => (
    <div className="space-y-6">
      <h3 className="text-2xl font-bold text-gray-800 mb-6">Feature Toggles</h3>
      <p className="text-gray-600 mb-6">Enable or disable features across your website. Changes apply instantly!</p>
      
      <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
        {config.features && Object.entries(config.features).map(([feature, enabled]) => (
          <div key={feature} className="flex items-center justify-between p-4 bg-gray-50 rounded-lg">
            <div>
              <label className="block text-sm font-semibold text-gray-700">
                {feature.replace(/([A-Z])/g, ' $1').replace(/^./, str => str.toUpperCase())}
              </label>
            </div>
            <label className="relative inline-flex items-center cursor-pointer">
              <input
                type="checkbox"
                checked={enabled || false}
                onChange={(e) => updateConfig(`features.${feature}`, e.target.checked)}
                className="sr-only peer"
              />
              <div className="w-11 h-6 bg-gray-200 peer-focus:outline-none peer-focus:ring-4 peer-focus:ring-orange-300 rounded-full peer peer-checked:after:translate-x-full peer-checked:after:border-white after:content-[''] after:absolute after:top-[2px] after:left-[2px] after:bg-white after:rounded-full after:h-5 after:w-5 after:transition-all peer-checked:bg-orange-500"></div>
            </label>
          </div>
        ))}
      </div>
    </div>
  );

  const renderCurrentSection = () => {
    switch (activeSection) {
      case 'basic': return renderBasicInfo();
      case 'character': return renderCharacterInfo();
      case 'social': return renderSocialMedia();
      case 'features': return renderFeatures();
      default: return <div className="p-8 text-center text-gray-500">Section coming soon...</div>;
    }
  };

  return (
    <div className="min-h-screen bg-gray-100 py-8">
      <div className="container mx-auto px-4 max-w-7xl">
        <div className="bg-white rounded-xl shadow-lg overflow-hidden">
          {/* Header */}
          <div className="bg-gradient-to-r from-orange-500 to-red-500 text-white p-8">
            <div className="flex items-center justify-between">
              <div>
                <h1 className="text-4xl font-bold mb-2">ü¶ä Fox Shrine Setup</h1>
                <p className="text-xl opacity-90">Configure your VTuber website settings</p>
              </div>
              <div className="text-right">
                <p className="text-sm opacity-75">‚ö° Reactive Configuration</p>
                <p className="text-xs opacity-50">Changes apply instantly!</p>
              </div>
            </div>
          </div>

          <div className="flex flex-col lg:flex-row">
            {/* Sidebar */}
            <div className="lg:w-80 bg-gray-50 border-r border-gray-200">
              <nav className="p-6">
                <h3 className="text-sm font-semibold text-gray-500 uppercase tracking-wider mb-4">Configuration</h3>
                {sections.map((section) => (
                  <button
                    key={section.id}
                    onClick={() => setActiveSection(section.id)}
                    className={`w-full text-left p-4 rounded-xl mb-3 transition-all duration-200 flex items-center ${
                      activeSection === section.id 
                        ? 'bg-orange-500 text-white shadow-lg transform scale-105' 
                        : 'bg-white text-gray-700 hover:bg-gray-100 shadow-sm hover:shadow-md'
                    }`}
                  >
                    <span className="text-2xl mr-4">{section.icon}</span>
                    <span className="font-semibold">{section.name}</span>
                  </button>
                ))}
              </nav>

              {/* Action Buttons */}
              <div className="p-6 border-t border-gray-200 space-y-3">
                <button
                  onClick={handleExportConfig}
                  className={`w-full px-6 py-3 rounded-lg font-semibold transition-all ${
                    saved 
                      ? 'bg-green-500 text-white' 
                      : 'bg-blue-500 hover:bg-blue-600 text-white shadow-md hover:shadow-lg'
                  }`}
                >
                  {saved ? '‚úÖ Downloaded!' : 'üíæ Export Config File'}
                </button>
                
                <button
                  onClick={handleResetConfig}
                  className="w-full bg-red-500 hover:bg-red-600 text-white px-6 py-3 rounded-lg font-semibold transition-all shadow-md hover:shadow-lg"
                >
                  üîÑ Reset to Default
                </button>
              </div>
            </div>

            {/* Main Content */}
            <div className="flex-1 p-8 lg:p-12">
              <div className="mb-6 p-4 bg-blue-50 border border-blue-200 rounded-lg">
                <p className="text-blue-800 font-medium">
                  ‚ö° Real-time Configuration: Changes you make here are applied instantly to the website!
                  You can see the updates immediately on other pages.
                </p>
              </div>
              
              {/* Debug Controls */}
              <div className="mb-6 p-4 bg-yellow-50 border border-yellow-200 rounded-lg">
                <p className="text-yellow-800 font-medium mb-3">üõ†Ô∏è Debug Tools:</p>
                <div className="flex gap-3 flex-wrap">
                  <button
                    onClick={() => console.log('Current config:', config)}
                    className="px-3 py-1 bg-yellow-500 text-white text-sm rounded hover:bg-yellow-600"
                  >
                    Log Config
                  </button>
                  <button
                    onClick={clearCorruptedConfig}
                    className="px-3 py-1 bg-red-500 text-white text-sm rounded hover:bg-red-600"
                  >
                    Clear & Reload
                  </button>
                  <button
                    onClick={() => updateConfig('character.name', 'Test Fox ' + Date.now())}
                    className="px-3 py-1 bg-green-500 text-white text-sm rounded hover:bg-green-600"
                  >
                    Test Update
                  </button>
                </div>
              </div>
              
              {renderCurrentSection()}
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default SetupPage;
