import React from 'react';
import { useConfig } from '../hooks/useConfig';

const StreamSchedule = () => {
  const { config } = useConfig();
  const scheduleData = config.schedule?.weekly ? Object.entries(config.schedule.weekly).map(([day, data]) => ({
    day: day.charAt(0).toUpperCase() + day.slice(1),
    time: data?.time || 'TBD',
    title: data?.title || 'Stream',
    description: data?.description || 'Join me for fun activities!',
    icon: data?.icon || 'üéÆ'
  })) : [
    {
      day: 'Monday',
      time: '7:00 PM - 10:00 PM',
      title: 'Chatting & Games',
      description: 'Starting the week with chill vibes and fun games',
      icon: 'üéÆ'
    },
    {
      day: 'Wednesday',
      time: '8:00 PM - 11:00 PM',
      title: 'Adventure Games',
      description: 'Join the journey through mysterious worlds',
      icon: 'üó∫Ô∏è'
    },
    {
      day: 'Friday',
      time: '9:00 PM - 12:00 AM',
      title: 'Fox Friday Funtime',
      description: 'End the week with maximum silliness and games',
      icon: 'ü¶ä'
    },
    {
      day: 'Sunday',
      time: '3:00 PM - 7:00 PM',
      title: 'Shrine Stories',
      description: 'Relax with shrine tales and community time',
      icon: '‚õ©Ô∏è'
    }
  ];
  return (
    <section className="py-16 bg-shrine-white">
      <div className="container mx-auto px-4">
        <h2 className="section-title text-center">Stream Schedule</h2>
        
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6 mt-12">
          {scheduleData.map((item, index) => (
            <div 
              key={index} 
              className="shrine-card hover:shadow-xl transition-shadow duration-300 transform hover:-translate-y-1"
            >
              <div className="text-4xl text-center mb-4">{item.icon}</div>
              <h3 className="font-cinzel text-xl text-shrine-red text-center mb-2">{item.day}</h3>
              <p className="text-center font-medium text-fox-orange mb-4">{item.time}</p>
              <h4 className="text-center font-bold mb-2">{item.title}</h4>
              <p className="text-center text-sm text-gray-600">{item.description}</p>
            </div>
          ))}
        </div>
        
        <div className="text-center mt-10">
          <a 
            href="/schedule" 
            className="fox-button inline-flex items-center"
          >
            <span>Full Schedule</span>
            <svg className="w-4 h-4 ml-2" fill="none" stroke="currentColor" viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 5l7 7-7 7" />
            </svg>
          </a>
        </div>
      </div>
    </section>
  );
};

export default StreamSchedule;
