import React, { Suspense, lazy } from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import { HelmetProvider } from 'react-helmet-async';
import { ConfigProvider } from './hooks/useConfigDatabase';
import { AuthProvider } from './hooks/useAuth';
import Navbar from './components/Navbar';
import Footer from './components/Footer';
import ScrollToTop from './components/ScrollToTop';
import LoadingFallback from './components/LoadingFallback';
import ErrorBoundary from './components/ErrorBoundary';

// Eagerly loaded components (critical for initial load)
import HomePage from './pages/HomePage';

// Lazy loaded components (non-critical for initial load)
const AboutPage = lazy(() => import('./pages/AboutPage'));
const LoginPage = lazy(() => import('./pages/LoginPage'));
const AdminDashboard = lazy(() => import('./components/AdminDashboard'));
const SetupPage = lazy(() => import('./pages/SetupPage'));

// Import other pages as they're created
// import SchedulePage from './pages/SchedulePage';
// import ContentPage from './pages/ContentPage';
// import MerchPage from './pages/MerchPage';
// import GalleryPage from './pages/GalleryPage';
// import ConnectPage from './pages/ConnectPage';

function App() {
  return (
    <HelmetProvider>
      <AuthProvider>
        <ConfigProvider>
          <Router>
            <ScrollToTop />
            <ErrorBoundary>
              <Navbar />
            </ErrorBoundary>
            <main>
              <ErrorBoundary>
                <Suspense fallback={<LoadingFallback />}>
                  <Routes>
                  <Route path="/" element={<HomePage />} />
                  {/* Add other routes as pages are created */}
                  <Route path="/about" element={<AboutPage />} />
                  {/* Authentication & User Pages */}
                  <Route path="/login" element={<LoginPage />} />
                  {/* Admin Dashboard - Protected Route */}
                  <Route path="/admin" element={<AdminDashboard />} />
                  {/* Development Only - Setup Page */}
                  {process.env.NODE_ENV === 'development' && (
                    <Route path="/setup" element={<SetupPage />} />
                  )}
          {/* <Route path="/schedule" element={<SchedulePage />} /> */}
          {/* <Route path="/content" element={<ContentPage />} /> */}
          {/* <Route path="/merch" element={<MerchPage />} /> */}
          {/* <Route path="/gallery" element={<GalleryPage />} /> */}
          {/* <Route path="/connect" element={<ConnectPage />} /> */}
          
                  {/* 404 Page */}
                  <Route path="*" element={
                    <div className="min-h-screen flex items-center justify-center">
                      <div className="text-center">
                        <h1 className="font-cinzel text-4xl text-shrine-red mb-4">Page Not Found</h1>
                        <p className="mb-8">Oops! Looks like our fox has hidden this page!</p>
                        <a href="/" className="fox-button">Return to Shrine</a>
                      </div>
                    </div>
                  } />
                  </Routes>
                </Suspense>
              </ErrorBoundary>
            </main>
            <Footer />
          </Router>
        </ConfigProvider>
      </AuthProvider>
    </HelmetProvider>
  );
}

export default App;
