import React from 'react';

class ErrorBoundary extends React.Component {
  constructor(props) {
    super(props);
    this.state = { 
      hasError: false, 
      error: null, 
      errorInfo: null,
      eventId: null 
    };
  }

  static getDerivedStateFromError(error) {
    return { hasError: true };
  }

  componentDidCatch(error, errorInfo) {
    console.error('ErrorBoundary caught an error:', error, errorInfo);
    
    this.setState({
      error: error,
      errorInfo: errorInfo
    });

    // Here you could send error to an error reporting service
    // Example: Sentry.captureException(error, { contexts: { react: { componentStack: errorInfo.componentStack } } });
  }

  handleRetry = () => {
    this.setState({ 
      hasError: false, 
      error: null, 
      errorInfo: null 
    });
  };

  render() {
    if (this.state.hasError) {
      const { fallback: Fallback } = this.props;
      
      if (Fallback) {
        return <Fallback error={this.state.error} retry={this.handleRetry} />;
      }

      return (
        <div className="min-h-screen flex items-center justify-center bg-shrine-white p-4">
          <div className="max-w-md w-full text-center">
            <div className="mb-6">
              <div className="w-20 h-20 mx-auto mb-4 text-6xl">
                ðŸ¦Š
              </div>
              <h1 className="font-cinzel text-3xl text-shrine-red mb-2">
                Oops! Something went wrong
              </h1>
              <p className="text-shrine-dark mb-6">
                Our mischievous fox has encountered an unexpected error. Don't worry, we're on it!
              </p>
            </div>

            <div className="bg-white rounded-lg p-6 shadow-lg mb-6">
              <details className="text-left">
                <summary className="cursor-pointer font-medium text-shrine-dark mb-2">
                  Technical Details
                </summary>
                <div className="text-sm text-gray-600 bg-gray-50 p-3 rounded mt-2">
                  <p className="font-mono text-xs break-all">
                    {this.state.error && this.state.error.toString()}
                  </p>
                  {process.env.NODE_ENV === 'development' && this.state.errorInfo && (
                    <details className="mt-2">
                      <summary className="cursor-pointer text-xs">Component Stack</summary>
                      <pre className="text-xs mt-1 whitespace-pre-wrap">
                        {this.state.errorInfo.componentStack}
                      </pre>
                    </details>
                  )}
                </div>
              </details>
            </div>

            <div className="space-y-3">
              <button
                onClick={this.handleRetry}
                className="w-full fox-button"
              >
                Try Again
              </button>
              <button
                onClick={() => window.location.href = '/'}
                className="w-full bg-shrine-white text-shrine-dark border-2 border-shrine-dark px-6 py-3 rounded-lg hover:bg-shrine-dark hover:text-white transition-colors"
              >
                Return to Home
              </button>
            </div>

            <p className="text-xs text-gray-500 mt-6">
              If this problem persists, please contact support.
            </p>
          </div>
        </div>
      );
    }

    return this.props.children;
  }
}

export default ErrorBoundary;

// Hook for functional components to handle errors
export const useErrorHandler = () => {
  const handleError = (error, errorInfo) => {
    console.error('Error caught by useErrorHandler:', error, errorInfo);
    // Here you could send to error reporting service
  };

  return handleError;
};
