import { useState, useEffect, createContext, useContext } from 'react';
import { siteConfig as defaultConfig } from '../config/siteConfig';

// Create context for configuration
const ConfigContext = createContext();

// Custom hook to use configuration
export const useConfig = () => {
  const context = useContext(ConfigContext);
  if (!context) {
    throw new Error('useConfig must be used within a ConfigProvider');
  }
  return context;
};

// Configuration provider component
export const ConfigProvider = ({ children }) => {
  const [config, setConfig] = useState(defaultConfig);

  // Load config from localStorage on mount
  useEffect(() => {
    const savedConfig = localStorage.getItem('foxShrineConfig');
    if (savedConfig) {
      try {
        const parsedConfig = JSON.parse(savedConfig);
        console.log('Loaded config from localStorage:', parsedConfig);
        
        // Deep merge with defaults to ensure all properties exist
        const mergeDeep = (target, source) => {
          const result = { ...target };
          for (const key in source) {
            if (source[key] && typeof source[key] === 'object' && !Array.isArray(source[key])) {
              result[key] = mergeDeep(target[key] || {}, source[key]);
            } else {
              result[key] = source[key];
            }
          }
          return result;
        };
        
        setConfig(mergeDeep(defaultConfig, parsedConfig));
      } catch (error) {
        console.error('Error loading saved config:', error);
        setConfig(defaultConfig);
      }
    }
  }, []);

  // Save config to localStorage whenever it changes (excluding initial load)
  useEffect(() => {
    // Only save if config is different from default (avoid saving on initial load)
    if (config !== defaultConfig) {
      localStorage.setItem('foxShrineConfig', JSON.stringify(config));
      console.log('Config saved to localStorage:', config);
    }
  }, [config]);

  // Update configuration function
  const updateConfig = (path, value) => {
    setConfig(prevConfig => {
      // CRITICAL FIX: Deep clone to ensure React detects changes
      const newConfig = JSON.parse(JSON.stringify(prevConfig));
      
      // Handle nested paths like 'character.name' or 'socialMedia.youtube'
      const pathArray = path.split('.');
      let current = newConfig;
      
      // Navigate to the parent of the target property
      for (let i = 0; i < pathArray.length - 1; i++) {
        if (!current[pathArray[i]] || typeof current[pathArray[i]] !== 'object') {
          current[pathArray[i]] = {};
        }
        current = current[pathArray[i]];
      }
      
      // Set the final value
      current[pathArray[pathArray.length - 1]] = value;
      
      console.log(`Config updated: ${path} = ${value}`, newConfig);
      return newConfig;
    });
  };

  // Reset config to defaults
  const resetConfig = () => {
    console.log('Resetting config to defaults');
    localStorage.removeItem('foxShrineConfig');
    setConfig(defaultConfig);
  };
  
  // Clear corrupted localStorage and restart fresh
  const clearCorruptedConfig = () => {
    console.log('Clearing potentially corrupted localStorage');
    localStorage.removeItem('foxShrineConfig');
    window.location.reload();
  };

  // Export current config as JSON
  const exportConfig = () => {
    const configContent = `// Fox Shrine VTuber Website Configuration
// This file contains all configurable content for the website

export const siteConfig = ${JSON.stringify(config, null, 2)};

// Helper Functions
export const getSocialLink = (platform) => {
  return siteConfig.socialMedia[platform] || '#';
};

export const getSupportLink = (platform) => {
  return siteConfig.support[platform] || '#';
};

export const isFeatureEnabled = (feature) => {
  return siteConfig.features[feature] || false;
};

export const getCharacterInfo = (field) => {
  return siteConfig.character[field] || '';
};

export const getStreamingInfo = (field) => {
  return siteConfig.streaming[field] || '';
};

export const getScheduleForDay = (day) => {
  return siteConfig.schedule.weekly[day.toLowerCase()] || null;
};
`;

    // Create download
    const blob = new Blob([configContent], { type: 'text/javascript' });
    const url = URL.createObjectURL(blob);
    const a = document.createElement('a');
    a.href = url;
    a.download = 'siteConfig.js';
    document.body.appendChild(a);
    a.click();
    document.body.removeChild(a);
    URL.revokeObjectURL(url);
  };

  // Helper functions that work with current config
  const getSocialLink = (platform) => {
    return config.socialMedia[platform] || '#';
  };

  const getSupportLink = (platform) => {
    return config.support[platform] || '#';
  };

  const isFeatureEnabled = (feature) => {
    return config.features[feature] || false;
  };

  const getCharacterInfo = (field) => {
    return config.character[field] || '';
  };

  const getStreamingInfo = (field) => {
    return config.streaming[field] || '';
  };

  const getNavigationItems = () => {
    return config.navigationMenu.filter(item => {
      // Hide certain items based on feature flags
      if (item.path === '/gallery' && !isFeatureEnabled('enableFanArtGallery')) {
        return false;
      }
      if (item.path === '/merch' && !isFeatureEnabled('showMerchSection')) {
        return false;
      }
      return true;
    });
  };

  const value = {
    config,
    updateConfig,
    resetConfig,
    clearCorruptedConfig,
    exportConfig,
    getSocialLink,
    getSupportLink,
    isFeatureEnabled,
    getCharacterInfo,
    getStreamingInfo,
    getNavigationItems
  };

  return (
    <ConfigContext.Provider value={value}>
      {children}
    </ConfigContext.Provider>
  );
};

export default ConfigProvider;
