import { useState, useEffect, createContext, useContext } from 'react';
import { siteConfig as defaultConfig } from '../config/siteConfig';

// Create context for configuration
const ConfigContext = createContext();

// API Configuration
const API_BASE_URL = process.env.REACT_APP_API_URL || 'http://localhost:3001/api';

// Custom hook to use configuration
export const useConfig = () => {
  const context = useContext(ConfigContext);
  if (!context) {
    throw new Error('useConfig must be used within a ConfigProvider');
  }
  return context;
};

// Configuration provider component
export const ConfigProvider = ({ children }) => {
  const [config, setConfig] = useState(defaultConfig);
  const [isLoading, setIsLoading] = useState(true);
  const [isOnline, setIsOnline] = useState(navigator.onLine);
  const [lastSynced, setLastSynced] = useState(null);

  // Network status monitoring
  useEffect(() => {
    const handleOnline = () => setIsOnline(true);
    const handleOffline = () => setIsOnline(false);
    
    window.addEventListener('online', handleOnline);
    window.addEventListener('offline', handleOffline);
    
    return () => {
      window.removeEventListener('online', handleOnline);
      window.removeEventListener('offline', handleOffline);
    };
  }, []);

  const loadConfigFromDatabase = async () => {
    try {
      setIsLoading(true);
      const response = await fetch(`${API_BASE_URL}/config`);
      
      if (!response.ok) {
        throw new Error(`HTTP ${response.status}`);
      }
      
      const data = await response.json();
      
      if (data.success && data.config) {
        console.log('âœ… Config loaded from database:', data.config);
        setConfig(prevConfig => ({
          ...defaultConfig,
          ...data.config
        }));
        setLastSynced(new Date());
        
        // Also save to localStorage as backup
        localStorage.setItem('foxShrineConfig', JSON.stringify(data.config));
        localStorage.setItem('foxShrineConfigTimestamp', data.lastUpdated || new Date().toISOString());
      } else {
        throw new Error(data.message || 'Invalid response format');
      }
    } catch (error) {
      console.warn('âš ï¸ Failed to load from database, using local fallback:', error);
      
      // Fallback to localStorage
      const savedConfig = localStorage.getItem('foxShrineConfig');
      if (savedConfig) {
        try {
          const parsedConfig = JSON.parse(savedConfig);
          setConfig(prevConfig => ({
            ...defaultConfig,
            ...parsedConfig
          }));
        } catch (parseError) {
          console.error('Failed to parse local config:', parseError);
          setConfig(defaultConfig);
        }
      }
    } finally {
      setIsLoading(false);
    }
  };

  // Load config from database on mount
  useEffect(() => {
    loadConfigFromDatabase();
  }, []);

  // Sync with database when coming back online
  useEffect(() => {
    if (isOnline && !lastSynced) {
      loadConfigFromDatabase();
    }
  }, [isOnline, lastSynced]);

  // Update configuration function with database sync
  const updateConfig = async (path, value) => {
    // Optimistic update - update local state immediately
    setConfig(prevConfig => {
      const newConfig = JSON.parse(JSON.stringify(prevConfig));
      const keys = path.split('.');
      let current = newConfig;
      
      for (let i = 0; i < keys.length - 1; i++) {
        if (!current[keys[i]] || typeof current[keys[i]] !== 'object') {
          current[keys[i]] = {};
        }
        current = current[keys[i]];
      }
      
      current[keys[keys.length - 1]] = value;
      
      // Save to localStorage immediately as backup
      localStorage.setItem('foxShrineConfig', JSON.stringify(newConfig));
      
      return newConfig;
    });

    // Sync to database if online
    if (isOnline) {
      try {
        const dataType = typeof value === 'boolean' ? 'boolean' : 
                        typeof value === 'number' ? 'number' :
                        typeof value === 'object' ? 'json' : 'string';

        const response = await fetch(`${API_BASE_URL}/config/update`, {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
          },
          body: JSON.stringify({
            path,
            value,
            dataType
          }),
        });

        const result = await response.json();
        
        if (result.success) {
          console.log(`âœ… Config synced to database: ${path} = ${value}`);
          setLastSynced(new Date());
        } else {
          throw new Error(result.message);
        }
      } catch (error) {
        console.error(`âŒ Failed to sync to database: ${path}`, error);
        // Note: Local state is already updated, so app continues to work
      }
    } else {
      console.log(`ðŸ“± Offline: Config saved locally, will sync when online: ${path} = ${value}`);
    }
  };

  // Reset config to defaults
  const resetConfig = async () => {
    if (isOnline) {
      try {
        const response = await fetch(`${API_BASE_URL}/config/reset`, {
          method: 'POST',
        });
        
        const result = await response.json();
        
        if (result.success) {
          console.log('âœ… Config reset in database');
          await loadConfigFromDatabase(); // Reload from database
        }
      } catch (error) {
        console.error('âŒ Failed to reset database config:', error);
      }
    }
    
    // Always reset local config
    localStorage.removeItem('foxShrineConfig');
    localStorage.removeItem('foxShrineConfigTimestamp');
    setConfig(defaultConfig);
  };

  // Export current config as JSON
  const exportConfig = () => {
    const configContent = `// Fox Shrine VTuber Website Configuration
// Generated on: ${new Date().toISOString()}
// Database sync: ${lastSynced ? lastSynced.toISOString() : 'Never'}

export const siteConfig = ${JSON.stringify(config, null, 2)};

// Helper Functions
export const getSocialLink = (platform) => {
  return siteConfig.socialMedia[platform] || '#';
};

export const getSupportLink = (platform) => {
  return siteConfig.support[platform] || '#';
};

export const isFeatureEnabled = (feature) => {
  return siteConfig.features[feature] || false;
};

export const getCharacterInfo = (field) => {
  return siteConfig.character[field] || '';
};

export const getStreamingInfo = (field) => {
  return siteConfig.streaming[field] || '';
};

export const getScheduleForDay = (day) => {
  return siteConfig.schedule.weekly[day.toLowerCase()] || null;
};
`;

    const blob = new Blob([configContent], { type: 'text/javascript' });
    const url = URL.createObjectURL(blob);
    const a = document.createElement('a');
    a.href = url;
    a.download = `foxshrine-config-${Date.now()}.js`;
    document.body.appendChild(a);
    a.click();
    document.body.removeChild(a);
    URL.revokeObjectURL(url);
  };

  // Helper functions that work with current config
  const getSocialLink = (platform) => {
    return config.socialMedia?.[platform] || '#';
  };

  const getSupportLink = (platform) => {
    return config.support?.[platform] || '#';
  };

  const isFeatureEnabled = (feature) => {
    return config.features?.[feature] || false;
  };

  const getCharacterInfo = (field) => {
    return config.character?.[field] || '';
  };

  const getStreamingInfo = (field) => {
    return config.streaming?.[field] || '';
  };

  const getNavigationItems = () => {
    return config.navigationMenu?.filter(item => {
      if (item.path === '/gallery' && !isFeatureEnabled('enableFanArtGallery')) {
        return false;
      }
      if (item.path === '/merch' && !isFeatureEnabled('showMerchSection')) {
        return false;
      }
      return true;
    }) || [];
  };

  const value = {
    config,
    updateConfig,
    resetConfig,
    exportConfig,
    loadConfigFromDatabase,
    getSocialLink,
    getSupportLink,
    isFeatureEnabled,
    getCharacterInfo,
    getStreamingInfo,
    getNavigationItems,
    
    // Status indicators
    isLoading,
    isOnline,
    lastSynced
  };

  return (
    <ConfigContext.Provider value={value}>
      {children}
    </ConfigContext.Provider>
  );
};

export default ConfigProvider;
