import React, { useState } from 'react';
import { siteConfig } from '../config/siteConfig';

const SetupPage = () => {
  const [config, setConfig] = useState(siteConfig);
  const [activeSection, setActiveSection] = useState('basic');

  const handleInputChange = (section, key, value, subKey = null) => {
    setConfig(prev => ({
      ...prev,
      [section]: subKey ? {
        ...prev[section],
        [key]: {
          ...prev[section][key],
          [subKey]: value
        }
      } : {
        ...prev[section],
        [key]: value
      }
    }));
  };

  const exportConfig = () => {
    const configString = `export const siteConfig = ${JSON.stringify(config, null, 2)};`;
    const blob = new Blob([configString], { type: 'text/javascript' });
    const url = URL.createObjectURL(blob);
    const a = document.createElement('a');
    a.href = url;
    a.download = 'siteConfig.js';
    a.click();
  };

  const sections = [
    { id: 'basic', name: 'Basic Info', icon: '🏠' },
    { id: 'character', name: 'Character', icon: '🦊' },
    { id: 'social', name: 'Social Media', icon: '📱' },
    { id: 'streaming', name: 'Streaming', icon: '📹' },
    { id: 'contact', name: 'Contact', icon: '📧' },
    { id: 'merch', name: 'Merchandise', icon: '🛍️' },
    { id: 'schedule', name: 'Schedule', icon: '📅' },
    { id: 'features', name: 'Features', icon: '⚙️' }
  ];

  const renderBasicInfo = () => (
    <div className="space-y-6">
      <h3 className="font-cinzel text-xl text-shrine-red mb-4">Basic Site Information</h3>
      
      <div>
        <label className="block text-sm font-medium mb-2">Site Title</label>
        <input
          type="text"
          value={config.siteTitle}
          onChange={(e) => handleInputChange('siteTitle', null, e.target.value)}
          className="w-full p-3 border rounded-lg focus:outline-none focus:ring-2 focus:ring-fox-orange"
        />
      </div>

      <div>
        <label className="block text-sm font-medium mb-2">Site Description</label>
        <textarea
          value={config.siteDescription}
          onChange={(e) => handleInputChange('siteDescription', null, e.target.value)}
          rows={3}
          className="w-full p-3 border rounded-lg focus:outline-none focus:ring-2 focus:ring-fox-orange"
        />
      </div>

      <div>
        <label className="block text-sm font-medium mb-2">Site URL</label>
        <input
          type="url"
          value={config.siteUrl}
          onChange={(e) => handleInputChange('siteUrl', null, e.target.value)}
          className="w-full p-3 border rounded-lg focus:outline-none focus:ring-2 focus:ring-fox-orange"
        />
      </div>
    </div>
  );

  const renderSocialMedia = () => (
    <div className="space-y-6">
      <h3 className="font-cinzel text-xl text-shrine-red mb-4">Social Media Links</h3>
      
      {Object.entries(config.socialMedia).map(([platform, url]) => (
        <div key={platform}>
          <label className="block text-sm font-medium mb-2 capitalize">
            {platform === 'youtube' ? 'YouTube' : platform === 'tiktok' ? 'TikTok' : platform}
          </label>
          <input
            type="url"
            value={url}
            onChange={(e) => handleInputChange('socialMedia', platform, e.target.value)}
            className="w-full p-3 border rounded-lg focus:outline-none focus:ring-2 focus:ring-fox-orange"
            placeholder={`https://${platform}.com/your-username`}
          />
        </div>
      ))}
    </div>
  );

  const renderStreaming = () => (
    <div className="space-y-6">
      <h3 className="font-cinzel text-xl text-shrine-red mb-4">Streaming Configuration</h3>
      
      <div>
        <label className="block text-sm font-medium mb-2">Twitch Channel</label>
        <input
          type="url"
          value={config.streaming.twitchChannel}
          onChange={(e) => handleInputChange('streaming', 'twitchChannel', e.target.value)}
          className="w-full p-3 border rounded-lg focus:outline-none focus:ring-2 focus:ring-fox-orange"
        />
      </div>

      <div>
        <label className="block text-sm font-medium mb-2">YouTube Channel</label>
        <input
          type="url"
          value={config.streaming.youtubeChannel}
          onChange={(e) => handleInputChange('streaming', 'youtubeChannel', e.target.value)}
          className="w-full p-3 border rounded-lg focus:outline-none focus:ring-2 focus:ring-fox-orange"
        />
      </div>

      <div>
        <label className="block text-sm font-medium mb-2">Latest Stream Embed (YouTube Video ID)</label>
        <input
          type="text"
          value={config.streaming.latestStreamEmbed}
          onChange={(e) => handleInputChange('streaming', 'latestStreamEmbed', `https://www.youtube.com/embed/${e.target.value}`)}
          className="w-full p-3 border rounded-lg focus:outline-none focus:ring-2 focus:ring-fox-orange"
          placeholder="dQw4w9WgXcQ"
        />
        <p className="text-sm text-gray-500 mt-1">Just the video ID, not the full URL</p>
      </div>
    </div>
  );

  const renderFeatures = () => (
    <div className="space-y-6">
      <h3 className="font-cinzel text-xl text-shrine-red mb-4">Feature Toggles</h3>
      
      {Object.entries(config.features).map(([feature, enabled]) => (
        <div key={feature} className="flex items-center justify-between p-4 border rounded-lg">
          <div>
            <label className="block text-sm font-medium capitalize">
              {feature.replace(/([A-Z])/g, ' $1').replace(/^./, str => str.toUpperCase())}
            </label>
            <p className="text-sm text-gray-500">
              {getFeatureDescription(feature)}
            </p>
          </div>
          <label className="relative inline-flex items-center cursor-pointer">
            <input
              type="checkbox"
              checked={enabled}
              onChange={(e) => handleInputChange('features', feature, e.target.checked)}
              className="sr-only peer"
            />
            <div className="w-11 h-6 bg-gray-200 peer-focus:outline-none peer-focus:ring-4 peer-focus:ring-fox-orange/30 rounded-full peer peer-checked:after:translate-x-full peer-checked:after:border-white after:content-[''] after:absolute after:top-[2px] after:left-[2px] after:bg-white after:rounded-full after:h-5 after:w-5 after:transition-all peer-checked:bg-fox-orange"></div>
          </label>
        </div>
      ))}
    </div>
  );

  const getFeatureDescription = (feature) => {
    const descriptions = {
      showLiveStatus: 'Display live streaming status indicator',
      enableNewsletter: 'Show newsletter signup forms',
      showMerchSection: 'Display merchandise section',
      enableFanArtGallery: 'Show fan art gallery page',
      showDonationLinks: 'Display donation/support links',
      enableScheduleCalendar: 'Show stream schedule calendar',
      showSocialShare: 'Enable social media sharing buttons'
    };
    return descriptions[feature] || 'Toggle this feature on/off';
  };

  const renderCurrentSection = () => {
    switch (activeSection) {
      case 'basic': return renderBasicInfo();
      case 'social': return renderSocialMedia();
      case 'streaming': return renderStreaming();
      case 'features': return renderFeatures();
      default: return <div>Section coming soon...</div>;
    }
  };

  return (
    <div className="min-h-screen bg-gray-50 py-8">
      <div className="container mx-auto px-4">
        <div className="bg-white rounded-lg shadow-lg overflow-hidden">
          {/* Header */}
          <div className="bg-gradient-to-r from-shrine-red to-fox-orange text-white p-6">
            <h1 className="font-cinzel text-3xl font-bold">🦊 Fox Shrine Setup</h1>
            <p className="mt-2 opacity-90">Configure your VTuber website settings</p>
          </div>

          <div className="flex">
            {/* Sidebar */}
            <div className="w-64 bg-gray-100 border-r">
              <nav className="p-4">
                {sections.map((section) => (
                  <button
                    key={section.id}
                    onClick={() => setActiveSection(section.id)}
                    className={`w-full text-left p-3 rounded-lg mb-2 transition-colors ${
                      activeSection === section.id 
                        ? 'bg-fox-orange text-white' 
                        : 'hover:bg-gray-200'
                    }`}
                  >
                    <span className="mr-3">{section.icon}</span>
                    {section.name}
                  </button>
                ))}
              </nav>

              {/* Export Button */}
              <div className="p-4 border-t">
                <button
                  onClick={exportConfig}
                  className="w-full bg-shrine-teal text-white px-4 py-2 rounded-lg hover:bg-shrine-teal/80 transition-colors"
                >
                  💾 Export Config
                </button>
              </div>
            </div>

            {/* Main Content */}
            <div className="flex-1 p-6">
              {renderCurrentSection()}
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default SetupPage;
